create table produits(
    id_produit number(20) primary key,
    nom varchar2(20),
    prix number(10,5)
);


Declare
  v_id produits.id_produit%Type;
  cursor c1_produits is (select id_produit, prix from produits);
  rec c1_produits%rowtype;
BEGIN
  for i in 1..5 loop
  insert into produits values(i,'prod'||i,10+i);
  end loop;
  open c1_produits;
  loop 
  fetch c1_produits into rec;
  dbms_output.put_line('id' || rec.id_produit || ' prix ' || rec.prix);
  exit when c1_produits%notfound;
  end loop;


END;


