create table employes(
    id_emp number(20) primary key,
    nom varchar2(20),
    poste varchar2(20),
    salaire number(20),
    anciennete number(20)
);


Declare
  cursor c is select * from employes for update of salaire;
  v_sal number(20);
BEGIN
  for i in 1..10 loop
  insert into employes values(i,'emp'||i, 'poste'||i, i*100, i-1);
  end loop;
  for rec in c loop
      if rec.anciennete < 2 then
        v_sal := rec.salaire * 1.05;
      end if;
      if rec.anciennete between 2 and 5 then
        v_sal := rec.salaire * 1.10;
      end if;
      if rec.anciennete > 5 then
      v_sal := rec.salaire * 1.15;
      end if;
      update employes set salaire = v_sal where current of c;
  end loop;
  for rec in c loop
     
      dbms_output.put_line('id' || rec.id_emp || ' nom ' || rec.nom || ' poste ' || rec.poste || ' salaire ' || rec.salaire || ' anciennete ' || rec.anciennete );
  end loop;

END;


